import React, { Component } from 'react';
import firebase from '../../firebase'
import './home.css'

class Home extends Component {

    componentDidMount() {
        firebase.app.ref('posts').once(value, (snapshot) => {
            let state = this.state;
            state.posts = []
            snapshot.forEach((childItem) => {
                state.posts.push({
                    key: childItem.key,
                    titulo: childItem.titulo,
                    imagem: childItem.imagem,
                    descricao: childItem.descrição,
                    autor: childItem.autor
                })
            })
        })

    }

    state = {
        posts: []
    }

    render() {
        return (
            <h1>Pagina Home</h1>
        )
    }
}

export default Home;
