import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import firebase from '../../firebase';
import './login.css'

class Login extends Component {

    constructor(props) {
        super(props)
        this.state = {
            email: '',
            passoword: ''
        }

        this.entrar = this.entrar.bind(this)
        this.login = this.login.bind(this)

    }

    entrar(e) {
        e.preventDefault();

        this.login();
    }

    login = async () => {
        const { email, passoword } = this.state;

        try {
            firebase.login(email, passoword).catch((error) => {
                if (error.code === 'auth/user-not-found') {
                    alert('Usuario não existe')
                } else (alert("erro" + error.code))
                return (null)
            })
            this.props.history.replace('/dashboard');
        } catch (error) { alert(error.message) }


    }

    render() {
        return (
            <div>
                <form onSubmit={this.entrar} id='login'>
                    <label>Email: </label><br />
                    <input type='email' autoComplete='off'
                        autoFocus value={this.state.email}
                        onChange={(e) => this.setState({ email: e.target.value })}
                        placeholder='email@text.com' /><br />
                    <label>Password: </label><br />
                    <input type='password' autoComplete='off'
                        value={this.state.passoword}
                        onChange={(e) => this.setState({ passoword: e.target.value })}
                        placeholder='Digite sua senha' />

                    <button type='submit'>Entrar</button>
                    <Link to='/register'>Não possui uma conta ainda ?</Link>
                </form>


            </div>
        )
    }
}

export default withRouter(Login);
