import React, { Component } from 'react';
import firebase from '../../firebase'
import './home.css'

class Home extends Component {

    componentDidMount() {
        firebase.app.ref('posts').once(value, (snapshot) => {
            let state = this.state;
            state.posts = []
            snapshot.forEach((childItem) => {
                state.posts.push({
                    key: childItem.key,
                    titulo: childItem.titulo,
                    imagem: childItem.imagem,
                    descricao: childItem.descrição,
                    autor: childItem.autor
                })
            })
            this.setState(state)
        })

    }

    state = {
        posts: []
    }

    render() {
        return (
            <section>
                {this.state.posts.map((post) => {
                    return (
                        <di>
                            <article>
                                <header>
                                    <strong>{post.titulo}</strong>
                                    <span>{post.autor}</span>
                                </header>
                                <header>
                                    <img src={post.imagem} alt='capa do post' />
                                    <footer>
                                        <p>{post.descrição}</p>
                                    </footer>
                                </header>
                            </article>
                        </di>
                    )
                })}
            </section>
        )
    }
}

export default Home;
