import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import firebase from '../../firebase';
import './register.css'

class Register extends Component {

    constructor(props) {
        super(props)
        this.state = {
            nome: '',
            email: '',
            password: ''
        }

        this.register = this.register.bind(this);
        this.onRegister = this.onRegister.bind(this);

    }

    register(e) {
        e.preventDefault();
        this.onRegister();

    }

    onRegister = async () => {
        try {

            const { nome, email, password } = this.state;

            await firebase.register(nome, email, password);
            this.props.history.replace('/dashboard')

        }
        catch (error) {
            alert(error.message + this.state.email)

        }

    }

    render() {
        return (
            <div>
                <h1 className='register-h1'>Novo usuario:</h1><br />
                <form onSubmit={this.register} id='register'>
                    <label>Nome: </label><br />
                    <input type='text' value={this.state.nome} autoFocus autoComplete='off'
                        onChange={(e) => this.setState({ nome: e.target.value })}
                        placeholder='Rafael' /><br />

                    <label>Email: </label><br />
                    <input type='text' value={this.state.email} autoComplete='off'
                        onChange={(e) => this.setState({ email: e.target.value })}
                        placeholder='test@test.com' /><br />

                    <label>Senha: </label><br />
                    <input type='password' value={this.state.password} autoComplete='off'
                        onChange={(e) => this.setState({ password: e.target.value })}
                        placeholder='Digite sua Senha' /><br />

                    <button type='submit'>Registar</button>
                </form>

            </div>
        )
    }
}

export default withRouter(Register);
